sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    var niutils = new validateDate();
    var isPast = niutils.isDatePast(current.start_date);

    // If the isDatePast method returns true, the date is in the past.
    if (isPast == true) {
        gs.addErrorMessage("Start date cannot be in the past.  Your request has not been saved to the database.");
        current.setAbortAction(true);
    }

    var compare = niutils.compareStartAndEnd(current.start_date, current.end_date);
    if (compare == true) {
        gs.addErrorMessage("Start date should be earlier than end date.  Your request has not been saved to the database.");
        current.setAbortAction(true);
    }

    var duration = current.duration;
    var balance = current.balance;
    if (duration >= balance) {
        gs.addErrorMessage('You can not take leave that exceeds your current leave balance');
        current.setAbortAction(true);
    }

})(current, previous);
