sys_script_include.script:
var validateDate = Class.create();
validateDate.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	compareStartAndEnd: function (startDate, EndDate){
		var start = new GlideDateTime(startDate);
		var end = new GlideDateTime(EndDate);

		if (end.before(start)){
			return true;
		}
		else{
			return false;
		}
	},

    type: 'validateDate'
};
